class Solution {
public:
    string longestPalindrome(string s) {
        if(s == ""){
            string answer = "";
            return answer;
        } else if( s == ""){
            string answer = "";
            return answer;
        } else if(s == ""){
            string answer = "";
            return answer;
        }
        string temp;
        int max = 1;
        string answer(s, 0, 1);
        int pos;
        for(int i = 0; i < s.size()-max; i++){
            pos = s.size() - 1;
            while((pos = s.rfind(s[i], pos)) != i){
                //cout << pos << endl;
                //bug: c++ 中 != 优先级比 = 优先级高
                string rs(s, i, (pos - i + 1));
                temp = rs;
                reverse(rs.begin(),rs.end()); 
                if((rs == temp) && rs.size() > max){
                    max = rs.size();
                    answer = temp;
                    break;
                }
                pos = pos - 1;//bug: 应该从找到的下一个字符开始
            }
        }
        return answer;
    }
};
